#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("numere.in");
ofstream fout("numere.out");
int pare(int x)
{
  while (x != 0)
  {
    if (x % 10 % 2 != 0)
      return 0;
    x = x / 10;
  }
  return 1;
}
int main()
{
  int n, x, i, maxim = -1;
  fin >> n;
  for (i = 1; i <= n; i++)
  {
    fin >> x;
    if (pare(x) == 1)
      fout << x << ' ';
    if (x > maxim)
      maxim = x;
  }

  fout << endl;

  if (maxim != -1)
    fout << maxim;
  fin.close();
  fout.close();
  return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("atestat.in");
ofstream fout("atestat.out");
void afis(int n)
{
  int r = 0;
  while (n != 0)
  {
    int cif = n % 10;
    r++;
    if (r % 2 == 0)
      fout << cif << ' ';
    n /= 10;
  }
}
int cerintab(int n)
{
  int y = 0, p = 1;
  while (n > 9)
  {
    y = y + (n % 10) * p;
    p *= 10;
    n /= 10;
  }
  y = y * 10 + n;
  return y;
}
int main()
{
  int n;
  fin >> n;
  afis(n);
  fout << endl;
  fout << cerintab(n);
  fin.close();
  fout.close();
  return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("date.in");
ofstream fout("date.out");
int n, v[101];

void citire()
{
  fin >> n;
  for (int i = 1; i <= n; i++)
    fin >> v[i];
}

int cmmdc(int a, int b)
{
  while (b)
  {
    int r = a % b;
    a = b;
    b = r;
  }
  return a;
}
void rezolare()
{
  struct // memorizez valoarea nr impar minimi si pozitia sa intr-un struct 
  {
    int nr = 999999999, poz;
  } imp;
  for (int i = 1; i <= n; i++)
  {
    if (v[i] % 2 == 1)
    {
      if (v[i] < imp.nr) // calculez nr impar si stochez in struct valoarea si pozitia unde se afla in vector 
      {
        imp.nr = v[i];
        imp.poz = i;
      }
    }
  }

  fout << imp.nr << " " << imp.poz << endl; // cerinta a
  fout << cmmdc(v[1], v[2]); // cerinta b
}

int main()
{
  citire();
  rezolare();
  fin.close();
  fout.close();
  return 0;
}

#include <iostream>
#include <fstream>
#include <vector>
using namespace std;
ifstream fin("date.in");
ofstream fout("date.out");

struct
{
    int nr_divizori, valoarea;
} maxi_divizori;

vector<int> divizorii_proprii(int x) // returneaza un vector ce contine divizorii proprii variabilei primite ca parametru in cazul nostru x
{
    vector<int> vec_divizori;
    for (int d = 2; d <= x / 2; d++)
    {
        if (x % d == 0)
            vec_divizori.push_back(d);
    }
    return vec_divizori;
}
void cerinta_a(int nr, vector<int> vec_divizori)
{
    // afiseaza numarul citit din fisier si dupa divizorii sai
    fout << nr << " ";
    for (int i = 0; i < vec_divizori.size(); i++)
        fout << vec_divizori[i] << " ";
    fout << endl;

    // cerinta b: de la fiecare numar citit din fisier se verifica lungimea vectorului ce contine divizorii numarului curent si se compara cu maxi. In caz ca gaseste astfel de numar ce are mai multi divizori decat maxi, in maxi se va stoca nr de divizori si valoarea numarul ce avea acei divizori.
    if (vec_divizori.size() > maxi_divizori.nr_divizori)
    {
        maxi_divizori.valoarea = nr;
        maxi_divizori.nr_divizori = vec_divizori.size();
    }
    // In caz ca am doua valori cu nr de divizori maxim se va stoca in maxi valorea mai mica
    else if (vec_divizori.size() == maxi_divizori.nr_divizori)
    {
        if (nr < maxi_divizori.valoarea)
        {
            maxi_divizori.valoarea = nr;
            maxi_divizori.nr_divizori = vec_divizori.size();
        }
    }
}

void input()
{
    int n;
    while (fin >> n)
    {
        cerinta_a(n, divizorii_proprii(n));
    }
    fout << maxi_divizori.valoarea; // cerinta b
}
int main()
{
    input();
    fin.close();
    fout.close();
    return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("Numere.in");
ofstream fout("Rezultat.out");
int x, a, y;
int prim(int x)
{
  if (x < 2)
    return 0;
  if (x == 2)
    return 1;
  if (x % 2 == 0)
    return 0;
  for (int d = 3; d * d <= x; d = d + 2)
    if (x % d == 0)
      return 0;
  return 1;
}
void afis(int a)
{
  int d = 2, p = 0;
  while (a != 1)
  {
    while (a % d == 0)
    {
      a /= d;
      p++;
    }
    if (p != 0)
      fout << d << ' ' << p << endl;
    d++;
    p = 0;
  }
}
int main()
{
  fin >> x;
  afis(x);
  if (prim(x))
    y = x;
  while (fin >> x)
  {
    if (prim(x) && y == 0)
      y = x;
  }
  fout << y;
  fin.close();
  fout.close();
  return 0;
}

#include <iostream>
