using namespace std;
ifstream fin("numere.in");
ofstream fout("numere.out");
int x, p = -1, u = -1, maxi = -1;
int palindrom(int x)
{
  int aux = x, ogl = 0;
  while (aux)
  {
    ogl = ogl * 10 + aux % 10;
    aux /= 10;
  }
  if (x == ogl)
    return 1;
  else
    return 0;
}
int main()
{
  while (fin >> x)
  {
    if (palindrom(x) == 1)
    {
      if (p == -1)
        p = x;
      u = x;
      if (x > maxi)
        maxi = x;
    }
  }
  fout << maxi << endl;
  fout << p << " " << u;
  fin.close();
  fout.close();
  return 0;
}

