int x, p = -1, u = -1, maxi = -1;
int nr_cif(int x)
{
  int nr = 0;
  while (x)
  {
    x = x / 10;
    nr++;
  }
  return nr;
}
int cmmdc(int a, int b)
{
  while (a != b)
  {
    if (a > b)
      a = a - b;
    else
      b = b - a;
  }
  return a;
}
int main()
{
  int n, x, mini = 99999, maxi = -1;
  fin >> n;
  for (int i = 1; i <= n; i++)
  {
    fin >> x;
    if (x % 2 == 1)
    {
      fout << nr_cif(x) << " ";
      if (mini > x) // mini maxi din cele impare sau din toate?
        mini = x;
      if (maxi < x)
        maxi = x;
    }
  }
  fout << endl;
  fout << cmmdc(mini, maxi);
  fin.close();
  fout.close();
  return 0;
}

