#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("numere.in");
ofstream fout("numere.out");
int n, k;
void ordonare(int v[], int n)
{
  int i, j;
  for (i = 1; i < n; i++)
    for (j = i + 1; j <= n; j++)
      if (v[i] > v[j])
      {
        int aux = v[i];
        v[i] = v[j];
        v[j] = aux;
      }
}
int palindrom(int x)
{
  int x2 = x, ogl = 0;

  while (x)
  {
    int c = x % 10;
    ogl = ogl * 10 + c;
    x = x / 10;
  }

  if (x2 == ogl)
    return 1;
  else
    return 0;
}

int nrcif(int x)
{
  int nr = 0;

  while (x)
  {
    int c = x % 10;
    nr = nr + 1;
    x = x / 10;
  }

  return nr;
}
int main()
{
  int n, v[1001], i;
  fin >> n;
  for (i = 1; i <= n; i++)
    fin >> v[i];
  for (i = 1; i <= n; i++)
    if (palindrom(v[i]) == 1)
      fout << v[i] << ' ';
  fout << endl;
  ordonare(v, n);
  for (i = 1; i <= n; i++)
    if (palindrom(v[i]) == 1 && nrcif(v[i]) % 2 == 1)
      fout << v[i] << ' ';
  fin.close();
  fout.close();
  return 0;
}

#include <iostream>
#include <fstream>
#include <cmath>
using namespace std;
ifstream fin("puncte.txt");
ofstream fout("puncte.txt");
struct punct
{
  int x, y;
} v[1000];
int n;
double dist(punct a, punct b)
{
  return sqrt((a.x - b.x) * (a.x - b.x) + (a.y - b.y) * (a.y - b.y));
}
int main()
{
  n = 0;
  while (fin >> v[n].x >> v[n].y)
  {
    n++;
  }
  int nr = 0;
  for (int i = 0; i < n; i++)
    if (v[i].x > 0 && v[i].y > 0)
      nr++;
  fout << nr << " ";
  double maxi = 0;
  for (int i = 0; i < n - 1; i++)
    for (int j = i + 1; j < n; j++)
    {
      double d = dist(v[i], v[j]);
      if (d > maxi)
        maxi = d;
    }
  fout << maxi;
  fin.close();
  fout.close();
  return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("fractii.in");
ofstream fout("fractii.out");
struct fractie{
    int numarator, numitor;
}v[101];
int n;
int cmmdc(int a, int b)
{
    while(b)
    {
        int r=a%b;
        a=b;
        b=r;
    }
    return a;
}
int cmmmc(int a, int b)
{
    return (a*b)/cmmdc(a,b);
}

void citire(int &n, fractie v[])
{
    fin >> n;
    for(int i=1; i<=n; i++)
        fin >> v[i].numarator >> v[i].numitor;
}
int cerinta1(int n, fractie v[])
{
    int cnt = 0;
    for(int i = 1;i <= n; i++)
    {

        if(cmmdc(v[i].numarator, v[i].numitor) == 1)
            cnt++;
    }
    return cnt;

}
int cerinta2(int n, fractie v[])
{
    int multiplu = v[1].numitor;
    for(int i=2; i<=n; i++)
    {
        multiplu = cmmmc(multiplu,v[i].numitor);
    }
    return multiplu;
}

int main()
{
    citire(n, v);
    fout << cerinta1(n, v) <<" " << cerinta2(n, v);
    return 0;
}

