using namespace std;
ifstream fin("tip5.in");
ofstream fout("tip5.out");
struct produs
{
    int cod, pret, cantitate;
    char denumire[21];
} v[101];
int n;

void citire(int &n, produs v[])
{
    fin >> n;
    fin.get();
    for (int i = 1; i <= n; i++)
    {
        fin >> v[i].cod >> v[i].pret >> v[i].cantitate;
        fin >> v[i].denumire;
        fin.get();
    }
}
void cerinta1(int n, produs v[])
{
    bool ok = 0;
    for (int i = 1; i <= n; i++)
    {
        if (v[i].cantitate > 10)
            if (ok == 0)
            {
                fout << v[i].denumire;
                ok = 1;
            }
            else
                fout << ", " << v[i].denumire;
    }
    if (ok == 0)
        fout << "NU EXISTA!";
}
void ordonare(int n, produs v[])
{
    for (int i = 1; i < n; i++)
        for (int j = i + 1; j <= n; j++)
            if (v[i].pret < v[j].pret)
            {
                produs aux = v[i];
                v[i] = v[j];
                v[j] = aux;
            }
}
void afis(int n, produs v[])
{
    for (int i = 1; i <= n; i++)
        fout << v[i].cod << ' ' << v[i].denumire << ' ' << v[i].pret << ' ' << v[i].cantitate << endl;
}

int total(int n, produs v[])
{

    int t = 0;
    for (int i = 1; i <= n; i++)
    {

        t = t + v[i].cantitate * v[i].pret;
    }
    return t;
}

int main()
{
    citire(n, v);
    cerinta1(n, v);
    fout << endl;
    ordonare(n, v);
    afis(n, v);
    fout << total(n, v);
    return 0;
}

#include <iostream>
#include <fstream>
#include <iomanip>
#include <cstring>
using namespace std;
ifstream fin("elevi.in");
ofstream fout("elevi.out");
struct elev
{
    int note[6];
    char nume[21];
    double media;
} v[101];
int n;

void citire(int &n, elev v[])
{
    fin >> n;
    fin.get();
    for (int i = 1; i <= n; i++)
    {
        int nr = 0, s = 0;
        for (int j = 1; j <= 5; j++)
        {
            fin >> v[i].note[j];
            fin.get();
            if (v[i].note[j] != 0)
            {
                nr++;
                s = s + v[i].note[j];
            }
        }
        if (nr > 0)
            v[i].media = 1.0 * s / nr;
        else
            v[i].media = 0;
        fin.get(v[i].nume, 20);
        fin.get();
    }
}
void ordonare(int n, elev v[])
{
    for (int i = 1; i < n; i++)
        for (int j = i + 1; j <= n; j++)
            if (v[i].media < v[j].media)
            {
                elev aux = v[i];
                v[i] = v[j];
                v[j] = aux;
            }
}
void ordonare2(int n, elev v[])
{
    for (int i = 1; i < n; i++)
        for (int j = i + 1; j <= n; j++)
            if (strcmp(v[i].nume, v[j].nume) > 0)
            {
                elev aux = v[i];
                v[i] = v[j];
                v[j] = aux;
            }
}
void afis(int n, elev v[])
{
    for (int i = 1; i <= n; i++)
        fout << fixed << setprecision(2) << v[i].media << ' ' << v[i].nume << endl;
}
int main()
{
    citire(n, v);
    ordonare(n, v);
    afis(n, v);
    ordonare2(n, v);
    afis(n, v);
    return 0;
}

