#include <iostream>
using namespace std;

int produs(int a, int b)
{
  int interval, p = 1;
  a > b ? interval = b : interval = a;
  for (int i = 1; i <= interval; i++)
  {
    if (a % i == 0 && b % i == 0)
      p *= i;
  }
  return p;
}

int main()
{
  cout << produs(20, 12); // 8
  return 0;
}

