int estePrim(int x)
{
    if (x == 1)
        return false;
    if (x % 2 == 0 && x != 2)
        return false;
    for (int d = 2; d <= x / 2; d++)
    {
        if (x % d == 0)
            return false;
    }
    return true;
}
void DNPI(int n)
{
    for (int d = 1; d <= n / 2; d++)
    {
        if (d % 2 && n % d == 0 && !estePrim(d))
            cout << d << " ";
    }
}
int main()
{
    int n;
    cin >> n;
    DNPI(n);
    return 0;
}

