#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("f.in");
ofstream fout("f.out");
int n, k;
struct
{
    int nr, nr_sufix, nr_prefix;
} v[1001];

int conditie(int x)
{
    int cnt = 0;
    while (x)
    {
        cnt++;
        x /= 10;
    }
    return cnt;
}

int sufix(int x)
{
    return (x / 10 % 10) * 10 + x % 10;
}

int prefix(int x)
{
    int p = 1;
    int nr_cif = conditie(x);
    while (nr_cif)
    {
        nr_cif--;
        p *= 10;
    }
    return x % p / (p / 100);
}

void citire()
{
    fin >> n;

    v[++k].nr = prefix(n);
    for (int i = 1; i <= k; i++)
    {
        if (v[i].nr == prefix(n))
            v[k].nr_prefix++;
    }

    v[++k].nr = sufix(n);
    for (int i = 1; i <= k; i++)
    {
        if (v[i].nr == sufix(n))
            v[k].nr_sufix++;
    }

    while (fin >> n)
    {

        if (conditie(n) > 2)
        {
            bool okp = false, oks = false;

            for (int i = 1; i <= k; i++)
            {
                if (v[i].nr == prefix(n))
                {
                    v[i].nr_prefix++;
                    okp = true;
                }
            }
            if (okp == false)
            {
                v[++k].nr = prefix(n);
                v[k].nr_prefix++;
            }

            for (int i = 1; i <= k; i++)
            {
                if (v[i].nr == sufix(n))
                {
                    v[i].nr_sufix++;
                    oks = true;
                }
            }
            if (oks == false)
            {
                v[++k].nr = sufix(n);
                v[k].nr_sufix++;
            }
        }
    }
}
int afis(int nr)
{
    if (nr == k)
    {
        if (v[nr].nr_prefix == v[nr].nr_sufix)
            return 1;
        return 0;
    }
    else if (v[nr].nr_prefix == v[nr].nr_sufix)
        return 1 + afis(++nr);

    return afis(++nr);
}
int main()
{
    citire();
    fout << afis(1);
    return 0;
}

