using namespace std;
int n, p, x;

void Putere(int n, int &x, int &p)
{
    int copyN = n;
    x = 2;
    while (copyN > 1)
    {
        p = 0;
        while (copyN % x == 0)
        {
            p++;
            copyN /= x;
        }
        if (pow(x, p) == n)
            break;
        x++;
        copyN = n;
    }
}

int main()
{
    cin >> n;
    Putere(n, x, p);
    cout << x << "^" << p;
    return 0;
}

