void ord(int v[], int cnt)
{
    for (int i = 1; i < cnt; i++)
        for (int j = i + 1; j <= cnt; j++)
        {
            if (v[i] > v[j])
            {
                int aux = v[i];
                v[i] = v[j];
                v[j] = aux;
            }
        }
}
void minimul()
{
    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= m; j++)
        {
            cnt = 0;
            if (v[i - 1][j])
                a[++cnt] = v[i - 1][j];
            if (v[i][j + 1])
                a[++cnt] = v[i][j + 1];
            if (v[i + 1][j])
                a[++cnt] = v[i + 1][j];
            if (v[i][j - 1])
                a[++cnt] = v[i][j - 1];
            ord(a, cnt);
            mi = a[1];
            if (v[i][j] < mi)
            {
                nr += mi - v[i][j];
                // fout<<v[i][j]<<" "<<mi<<endl;
            }
        }
    }
    fout << nr;
