#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("f.in");
ofstream fout("f.out");
int x, n, cx, nr = 1, start, cn, term1, term2;
void citire()
{
    fin >> x;
    cx = x;
    while (cx)
    {
        nr *= 10;
        cx /= 10;
    }
}
void rezolvare()
{
    while (fin >> n)
    {
        if (start)
        {
            if (cn % nr == x && n % nr == x)
            {
                term1 = cn;
                term2 = n;
            }
        }
        cn = n;
        start = 1;
    }
    fout << term1 << " " << term2;
}
int main()
{
    citire();
    rezolvare();
    return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("f.in");
ofstream fout("f.out");
int x, y, og, rez;
int oglindit(int n)
{
    int og = 0;
    while (n)
    {
        if (n % 2)
            og = og * 10 + n % 10;
        n /= 10;
    }
    return og;
}
void imog()
{
    fin >> x >> y;
    if (oglindit(oglindit(x)) == oglindit(y))
        rez = 1;
    else
        rez = 0;
    fout << rez;
}
int main()
{
    imog();
    return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("f.in");
ofstream fout("f.out");
int n, k, v[101][101];
void citire()
{
    fin >> n >> k;
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= n; j++)
            fin >> v[i][j];
}
void rezolvare()
{
    for (int j = 1; j <= n; j++)
    {
        if (k > j)
        {
            int aux = v[k][j];
            v[k][j] = v[j][k];
            v[j][k] = aux;
        }
    }
}
void afis()
{
    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= n; j++)
            fout << v[i][j] << " ";
        fout << endl;
    }
}
int main()
{
    citire();
    rezolvare();
    afis();
    return 0;
}

#include <iostream>
#include <fstream>
using namespace std;
ifstream fin("f.in");
ofstream fout("f.out");
int x, y, ok = 1, cnt;
void citire()
{
    fin >> x >> y;
}
void rezolvare()
{
    for (int i = 1; ok == 1; i++)
    {
        if (y - (2 * i) == x)
        {
            cnt = i;
            ok = 0;
        }
    }
    fout << y << " ";
    for (int i = cnt; i >= 1; i--)
    {
        fout << y - (2 * i) << " ";
        y = y - (2 * i);
    }
}
int main()
{
    citire();
    rezolvare();
    return 0;
}
